var ApiVersionHandler = (function () {
    function ApiVersionHandler(apiVersion) {
        this.apiVersion = apiVersion;
    }
    ApiVersionHandler.prototype.prepareRequest = function (options) {
        if (options.headers && options.headers['Accept']) {
            options.headers["Accept"] = options.headers['Accept'] + '; api-version=' + this.apiVersion;
        }
    };
    return ApiVersionHandler;
})();
exports.ApiVersionHandler = ApiVersionHandler;
